import React, { Component } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Image,
  Button,
  ImageBackground
} from 'react-native';
import Icon from '@expo/vector-icons/Ionicons';
import { ScrollView }
  from 'react-native-gesture-handler'
import Deck from '../components/Deck';
import Cards from '../components/Cards';
import RealoadIcon from '../components/ReloadIcon';
import Details from '../screens/Details'
import Geocoder from 'react-native-geocoding';

const DATA = [
  {
    id: 1,
    title: "CASOS DE COVID EN LAS ULTIMAS 24hrs",
    number: "38 456"
  },
  {
    id: 2,
    title: "FALLECIMIENTOS EN LAS ULTIMAS 24hrs",
    number: "29 863"
  },
  {
    id: 3,
    title: "RECUPERADOS EN LAS ULTIMAS 24hrs",
    number: "8 456"
  }
]

export default class Home extends Component {

  constructor(props) {
    super(props);

    this.state = {
      
      loading: false,
      totals: [],
      byCountryName: [],
      urlTotals: 'https://covid-19-data.p.rapidapi.com/totals',
      urlByCountryName: 'https://covid-19-data.p.rapidapi.com/country?name='
    };
  }

  getData() {
    Geocoder.init("AIzaSyCmvhVA_FulaDlmSz0BfzevC2nUWGP1puk");
    
    Geocoder.from(41.89, 12.49)
      .then(json => {
        var addressComponent = json.results[0].address_components[0];
        console.log(addressComponent);
      })
      .catch(error => console.warn(error));
  }

  renderCard(item) {
    return (
      <View key={item.id} style={styles.cardContainer}>
        <View style={styles.card}>
          <View>
            <Text style={styles.title}>{item.title}</Text>
            <Icon
              name="ios-remove"
              size={40}
              color="red"
              style={{ marginTop: 25 }}
            />
            <Text style={styles.number}>{item.number}</Text>
          </View>
          <View style={{ marginLeft: 150 }}>
            <Icon name="md-options" size={24} color="#FFF" />
            <Text style={styles.textCovid}>COVID-19</Text>
          </View>
        </View>
      </View>
    );
  }

  renderNoMoreCards() {
    return (
      <View title="All Done!">

      </View>
    );
  }

  render() {
    return (
      <View style={styles.container}>
        <ImageBackground
          source={require("../images/mapamundi.png")}
          style={styles.map}
        >
          <View style={styles.col}>
            <View style={{ width: "50%" }}>
              <Icon name="md-remove" color="#FFF" size={26} />
              <Icon
                name="md-remove"
                color="#FFF"
                size={26}
                style={styles.minusIcon}
              />
            </View>
            <View style={styles.avatarContainer}>
              <Image
                source={require('../images/logo.png')}
                style={styles.avatar}
              />
            </View>
          </View>
          <Text style={styles.textDash}>Covid-19 World Statistics</Text>

          <View style={styles.colContainer}>
            <View>
              <RealoadIcon
              />
            </View>
          </View>
        </ImageBackground>
        <Deck
          data={DATA}
          renderCard={this.renderCard}
          renderNoMoreCards={this.renderNoMoreCards}
        />
        <ScrollView
          style={{ marginTop: 170 }}
          showsHorizontalScrollIndicator={false}
          horizontal
        >
          <Cards
            onPress={() => this.props.navigation.navigate('Details')}
            icon="md-medkit"
            title="CASOS TOTALES"
            bg="red"
            number="1 113 329"
          />
          <Cards
            onPress={() => this.props.navigation.navigate('Details')}
            icon="md-happy"
            title="RECUPERADOS"
            bg="#FFF"
            number="989 329"
          />
          <Cards
            onPress={() => this.props.navigation.navigate('Details')}
            icon="ios-heart-dislike"
            title="FALLECIMIENTOS"
            bg="#FFF"
            number="113 329"
          />
        </ScrollView>
      </View>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#18191A"
  },
  cardContainer: {
    height: 150,
    width: 320,
    alignSelf: "center",
    backgroundColor: "#3A3B3C",
    borderRadius: 30
  },
  card: {
    height: 150,
    width: 260,
    paddingTop: 20,
    paddingHorizontal: 30,
    backgroundColor: '#242526',
    borderRadius: 30,
    flexDirection: 'row'
  },
  title: {
    color: "#B7B9BE",
    width: 100,
    fontSize: 12,
    fontWeight: "bold"
  },
  number: {
    color: "#FFF",
    width: 100,
    fontSize: 22,
    fontWeight: "bold",
    marginTop: -10,
  },
  textCovid: {
    transform: [{ rotate: "-90deg" }],
    color: "#bebebe",
    fontSize: 14,
    width: 90,
    marginLeft: -35,
    fontWeight: 'bold',
    marginTop: 20
  },
  noCard: {
    marginTop: 100,
    color: '#FFF',
    alignSelf: "center",
    backgroundColor: "#03A9F4"
  },
  map: {
    marginTop: 10,
    height: 140,
    paddingTop: 25,
    paddingHorizontal: 20,
    marginBottom: 0
  },
  col: {
    flexDirection: 'row'
  },
  minusIcon: {
    marginTop: -20,
    marginLeft: 5
  },
  avatarContainer: {
    width: "50%",
    alignItems: 'flex-end',
  },
  avatar: {
    width: 40,
    height: 40,
    borderRadius: 20
  },
  textDash: {
    color: "#FFF",
    fontSize: 20,
    alignSelf: 'center',
    marginTop: 15,
    fontWeight: 'bold'
  },
  colContainer: {
    flexDirection: "row",
    paddingHorizontal: 30,
    marginTop: 40,
    alignItems: 'center',
  },
  textGlobal: {
    fontWeight: "bold",
    fontSize: 16,
    color: "orange"
  },
  textCountryUser: {
    fontWeight: "bold",
    fontSize: 16,
    paddingHorizontal: 30,
    color: "#BEBEBE"
  },
  reloadContainer: {
    backgroundColor: "#FFF",
    elevation: 2,
    width: 40,
    height: 40,
    borderRadius: 20,
    alignItems: 'center',
    justifyContent: "center",
    marginLeft: 240
  }


});
