import React, { Component } from 'react';
import { View, StyleSheet, Image, Text } from 'react-native';
import Icon from '@expo/vector-icons/Ionicons';
import Chart from '../components/Chart';

export default class Details extends Component {
  render() {
    return (
      <View style={styles.page}>
        <View style={styles.headContainer}>
          <View style={styles.humContainer}>
            <Icon
              name="md-remove"
              size={26}
            />
            <Icon
              name="md-remove"
              size={26}
              style={styles.hum}
            />
          </View>
          <View style={styles.profileContainer}>
            <Image
              source={require('../images/logo.png')}
              style={styles.profile}
            />
          </View>
        </View>
        <View style={styles.optionCard}>
          <View style={styles.optionCol}>
            <Text style={styles.textLinear}>Fallecimientos Totales</Text>
          </View>
        </View>
        <Chart />

      </View>
    )
  }
}
const styles = StyleSheet.create({
  page: {
    backgroundColor: "#FFF",
    flex: 1
  },
  headContainer: {
    marginHorizontal: 20,
    flexDirection: 'row',
    marginTop: 40
  },
  humContainer: {
    width: "50%"
  },
  hum: {
    marginTop: -20,
    marginLeft: 5
  },
  profileContainer: {
    width: "50%",
    alignItems: "flex-end"
  },
  profile: {
    width: 40,
    height: 40,
    borderRadius: 20
  },
  optionCard: {
    flexDirection: "row",
    alignItems: "center",
    marginHorizontal: 110,
  },
  optionCol: {
    backgroundColor: "#000",
    paddingVertical: 2,
    paddingHorizontal: 5,
    borderRadius: 2
  },
  textLinear: {
    color: "#FFF",
    fontSize: 12,
    fontWeight: "bold"
  },
  textLogarthimic: {
    color: "#b8b8aa",
    fontWeight: "bold",
    fontSize: 12,
    marginLeft: 15
  },
  locationContainer: {
    alignSelf: "center",
    flexDirection: "row",
    paddingHorizontal: 30,
    marginTop: 40,
    alignItems: "center"
  },
  textGlobal: {
    fontWeight: "bold",
    fontSize: 16,
    color: "red"
  },
  textCountryUser: {
    fontWeight: "bold",
    fontSize: 16,
    color: "#6a706e",
    paddingHorizontal: 30
  },
  reloadContainer: {
    backgroundColor: "#FFF",
    width: 40,
    height: 40,
    borderRadius: 20,
    alignItems: 'center',
    justifyContent: 'center',
    marginLeft: 50,
    elevation: 3
  },
  bottomCard: {
    backgroundColor: "#1c2732",
    height: 220,
    marginTop: 0,
    borderTopLeftRadius: 30,
    borderTopRightRadius: 30,
  },
  bottomCol: {
    flexDirection: 'row',
    alignItems: 'center',
    alignSelf: 'center',
    marginTop: 20
  },
  textSymptoms: {
    color: "#FFF",
    fontSize: 12,
    fontWeight: 'bold'
  },
  infoContainer: {
    width: 20,
    height: 20,
    borderRadius: 10,
    backgroundColor: "red",
    alignItems: "center",
    justifyContent: 'center',
    marginLeft: 10,
  },
  button: {
    borderRadius: 15,
    borderColor: 'red',
    borderWidth: 1,
    marginHorizontal: 30,
    paddingHorizontal: 20,
    paddingVertical: 15,
    alignItems: 'center',
    marginTop: 50,
    marginBottom: 5
  },
  btnText: {
    color: "red",
    fontSize: 16,
    fontWeight: 'bold'
  }


})
